declare const typeDefs = "\n  type User {\n    id: ID!\n    username: String!\n    email: String!\n    avatar: String\n    role: UserRole!\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  enum UserRole {\n    STUDENT\n    INSTRUCTOR\n    ADMIN\n  }\n\n  type Classroom {\n    id: ID!\n    name: String!\n    instructor: User!\n    description: String!\n    students: Int!\n    progress: Float!\n    nextClass: String\n    assignments: Int!\n    topics: [String!]!\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  type Community {\n    id: ID!\n    name: String!\n    description: String!\n    members: Int!\n    topics: [String!]!\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  type Post {\n    id: ID!\n    author: User!\n    title: String!\n    content: String!\n    tags: [String!]!\n    likes: Int!\n    comments: Int!\n    createdAt: String!\n    updatedAt: String!\n  }\n\n  type ChatMessage {\n    id: ID!\n    sender: User!\n    content: String!\n    communityId: ID\n    classroomId: ID\n    createdAt: String!\n  }\n\n  type Query {\n    # User queries\n    me: User\n    user(id: ID!): User\n    users: [User!]!\n\n    # Classroom queries\n    classroom(id: ID!): Classroom\n    classrooms(page: Int, limit: Int): [Classroom!]!\n    myClassrooms: [Classroom!]!\n\n    # Community queries\n    community(id: ID!): Community\n    communities(page: Int, limit: Int): [Community!]!\n    myCommunities: [Community!]!\n\n    # Post queries\n    post(id: ID!): Post\n    posts(page: Int, limit: Int): [Post!]!\n    postsByTag(tag: String!, page: Int, limit: Int): [Post!]!\n\n    # Chat queries\n    messages(communityId: ID, classroomId: ID, limit: Int): [ChatMessage!]!\n  }\n\n  input CreateClassroomInput {\n    name: String!\n    description: String!\n    topics: [String!]!\n  }\n\n  input UpdateClassroomInput {\n    name: String\n    description: String\n    topics: [String!]\n  }\n\n  input CreateCommunityInput {\n    name: String!\n    description: String!\n    topics: [String!]!\n  }\n\n  input UpdateCommunityInput {\n    name: String\n    description: String\n    topics: [String!]\n  }\n\n  input CreatePostInput {\n    title: String!\n    content: String!\n    tags: [String!]!\n  }\n\n  input UpdatePostInput {\n    title: String\n    content: String\n    tags: [String!]\n  }\n\n  type Mutation {\n    # Classroom mutations\n    createClassroom(input: CreateClassroomInput!): Classroom!\n    updateClassroom(id: ID!, input: UpdateClassroomInput!): Classroom!\n    deleteClassroom(id: ID!): Boolean!\n\n    # Community mutations\n    createCommunity(input: CreateCommunityInput!): Community!\n    updateCommunity(id: ID!, input: UpdateCommunityInput!): Community!\n    deleteCommunity(id: ID!): Boolean!\n\n    # Post mutations\n    createPost(input: CreatePostInput!): Post!\n    updatePost(id: ID!, input: UpdatePostInput!): Post!\n    deletePost(id: ID!): Boolean!\n    likePost(id: ID!): Post!\n    unlikePost(id: ID!): Post!\n\n    # Chat mutations\n    sendMessage(content: String!, communityId: ID, classroomId: ID): ChatMessage!\n  }\n\n  type Subscription {\n    messageReceived(communityId: ID, classroomId: ID): ChatMessage!\n    postCreated: Post!\n    postLiked: Post!\n  }\n";
export default typeDefs;
